import Vue from 'vue'
import VueRouter from 'vue-router'

import Home from '@/views/home'
import Login from '@/views/login'

Vue.use(VueRouter)

const router = new VueRouter({
  routes: [
    // 数组里面包对象
    { path: '/', component: Home },
    { path: '/login', component: Login },
    { path: '/search', component: Search }

  ]
})

export default router
