// 购物车
import { getCartList } from '@/api/cart.js'

export default {
  namespaced: true,
  state () {
    return {
      cartList: []
    }
  },
  mutations: {
    // 提供一个 设置 cartList 的mutation
    setCartList (state, newList) {
      state.cartList = newList
    }
  },
  actions: {
    // 获取购物车列表
    async getCartAction (context) {
      const { data } = await getCartList()
      // 后台返回的数据中，不包含复选框的选中状态；
      // 为了实现将来的功能，需要手动维护数据，
      // 给每一项，添加一个 isChecked 状态（标记当前商品是否选中）
      context.commit('setCartList', data.list)
    }
  },
  getters: {}
}
