import request from '@/utils/request.js'

// 加入购物车
// goodsId => 商品id  ipone8
// goodsSkuId =>  商品规格id  红色的iphone8 粉色的iphone8
export const addCart = (goodsId, goodsName, goodsSkuId) => {
  return request.post('/cart/add', {
    params: {
      goodsId,
      goodsName,
      goodsSkuId
    }
  })
}
