import request from '@/utils/request.js'

// 收获地址列表
export const getAddressList = () => {
  return request.get('/address/list')
}

// 添加收获地址
export const getAddress = (name, phone, region) => {
  return request.post('address/add', {
    // params: {
    name,
    phone,
    region: []
    // }
  })
}
