// 购物车
import { getCartList } from '@/api/cart.js'

export default {
  namespaced: true,
  state () {
    return {
      cartList: []
    }
  },
  mutations: {
    // 提供一个 设置 cartList 的mutation
    setCartList (state, newList) {
      state.cartList = newList
    }
  },
  actions: {
    // 获取购物车列表
    async getCartAction () {
      const res = await getCartList()
      console.log(res)
    }
  },
  getters: {}
}
